def get_meta_info():
    meta_info = {}
    meta_info['magnification_ratio'] = 18.43107
    meta_info['directory_name'] = os.getenv('TOOTH_DATA') + '/stl_g20_shaper/'
    meta_info['bounding_box_range'] = [(0, 60), (30, 120), (0, 200)]
    meta_info['canal_sound_suffix'] = '.sound-canal-cog.stl'
    meta_info['canal_first_suffix'] = '.reg-canal-cog.stl'
    meta_info['canal_second_suffix'] = '.S45-canal-cog.stl'
    meta_info['body_suffix'] = '.sound-solid-body.stl'
    # Canal class에서 canal 이름 붙일때  사용
    meta_info['canal_list'] = ['sound', 'reg', 'S45']
    return meta_info

specimens = []

# 각 specimen의  데이터를 읽음
specimen_infos = get_specimen_infos()

# specimen전체에 적용되는 공통 정보들
meta_info = get_meta_info()

'''
canal 이름 : mb. or ml. + suffix(sound, t04, t06, reg, S45)
  ==> mb.sound, ml.t04
'''
# suffix = 'sound t04 t06'.split()

'''
Analysis 
mb.sound --> mb.sound를 기준으로 mb.first(t04, reg) 와 mb.second(t06, S45) 비교
mb.t04 --> mb.t04를 기준으로 mb.second(t06) 비교

'''
#canals_to_analyze = ['mb.sound', 'ml.sound', 'mb.reg', 'ml.reg']
canals_to_analyze = ['mb.sound', 'ml.sound']
# canals_to_analyze = ['mb.reg', 'ml.reg']

for sp_info in specimen_infos:
    for ana in canals_to_analyze:
        s = Specimen(sp_info['name']+ana)
        s.tooth_position = sp_info['position']
        s.note = sp_info['note']
        s.canal_type = sp_info['type']

        if 'sound' in ana:
            canals = fill_canals_for_sound(sp_info['pts_canals'], meta_info['canal_list'], is_buccal='mb' in ana)
        elif 'reg' in ana:
            canals = fill_canals_for_first_prep(sp_info['pts_canals'], meta_info['canal_list'], is_buccal='mb' in ana)
        else:
            canals = None
            print('Raise Error condition')

        s = fill_specimen(meta_info, s, canals)
        specimens.append(s)

